INSERT INTO `CINEMA` VALUES(39, 'La profecía (The Omen)', 1976, 'Richard Donner', 'Gregory Peck (Robert Thorn), Lee Remick (Katherine Thorn), David Warner (Jennings), Billie Whitelaw (Mrs. Baylock), Harvey Stephens (Damien), Patrick Troughton (Father Brennan), Martin Benson (Father Spiletto), Robert Rietty (Monk), Tommy Duggan (Priest), John Stride (The Psychiatrist), Anthony Nicholls (Dr. Becker), Holly Palance (Nanny), Roy Boyd (Reporter), Patrick McAlinney (Photographer)', 2, 1, 'L\\''embaixador americà a Roma, es veu implicat en l\\''adopció del seu fill en estranyes circumstàncies... ', '\\"Here is wisdom. Let him that hath understanding count the number of the beast: for it is number of a man; and his number is 666.\\" Book of Revelation Chapter 13 Verse 18...', 2, 4, 'yes', 0, 'Jerry Goldsmith ', 'Gilbert Taylor, B.S.C.', 'yes', '', '');
