INSERT INTO `CINEMA` VALUES(20, 'Lucy', 200, 'Luc Besson', 'Scarlett Johansson, Morgan Freeman, Choi Min-Sik, Amr Waked, Jan Oliver Schroeder', 2, 4, 'Una noia és obligada a fer de \\"mula\\" d\\''una potent droga que li estimula el cervell fins adquirir un control del 100% de la capacitat...', 'Choi Min-Sik és el protagonista d\\''Old Boy\r\n\r\nL\\''home utilitza el 10% i el dofí un 20%...\r\n\r\nFotografía:', 4, 4, 'no', 0, '', '', 'no', '', '');
INSERT INTO `CINEMA` VALUES(21, '2001: Una odisea del espacio (2001: A space odyssey)', 1968, 'Stanley Kubrick', 'Keir Dullea, Gary Lockwood, William Sylvester, Daniel Richter, Leonard Rossiter, Margaret Tyzak, Robert Beatty, Sean Sullivan, Douglas Rain, Frank Miller, Glenn Beck, Edwina Carroll, Bill Weston, Alan Gifford, Penny Brahms, Mike Lovell, Edward Bishop, Ann Gillis, Heather Downham', 2, 11, '', 'Recordar que la millor forma de veure-la és en loop amb la Hilde dormint amb mí al sofà durant un cap de setmana sencer, i que ella només acabi recordant les escenes dels micos...\r\n\r\nScreenplay Arthur C. Clarke', 1, 4, 'no', 0, 'György Ligeti', 'Geoffrey Unsworth, B.S.C.', 'yes', '', '');
INSERT INTO `CINEMA` VALUES(42, 'Heat', 1995, 'Michael Mann', 'Robert de Niro, Al Pacino, Val Kilmer, Natalie Portman', 2, 4, 'Neil McCauley és un expert lladre. La seva filosofia és viure sense lligams si les coses es compliquen massa. La seva banda la formen criminals professionals tan qualificats que poden impresionar al detectiu Vincent Hanna, un home que viu tan obsessionat amb el seu treball que arriba a posar en perill la seva vida sentimental...', 'La Natalie Portman fa de filla (que s\\''intenta suicidar tallant-se la femoral a una banyera) de la parella del Al Pacino...', 4, 4, 'no', 0, '', '', 'no', '', '');
INSERT INTO `CINEMA` VALUES(23, '3 días para matar (3 days to kill)', 200, 'McG', 'Kevin Costner, Amber Heard, Hailee Steinfeld, Connie Nielsen', 2, 4, 'Un agent de la CIA amb una enfermetat terminal es veu involucrat en una feina complicada per aconseguir una medicació experimental que el curi, al mateix temps que restablir relacions amb la seva filla adolescent i la seva exdona.', 'Basat en una història de Luc Besson, que també es co-guionista.', 5, 4, 'no', 0, '', 'Thierry Arbogast', 'yes', '', '');
INSERT INTO `CINEMA` VALUES(24, 'Super 8', 2011, 'J. J. Abrams', 'Joel Courtney, Riley Griffiths, Elle Fanning, Ryan Lee, Gabriel Basso, Ron Eldard, Zach Mills, Kyle Chandler', 2, 7, 'Situada l\\''any 1979, a una petita població d\\''Ohio. Joe i els seus amics durant l\\''estiu, roden una pel·lícula de zombis en Super 8 quan veuen com s\\''estimba una furgoneta contra un tren, provocant el descarrilament i un terrible accident...', '', 4, 4, 'no', 0, '', '', 'no', '', '');
INSERT INTO `CINEMA` VALUES(25, 'Artic Blue', 1993, 'Peter Masterson', 'Rutger Hauer, Dylan Walsh, Rya Kihlstedt', 2, 4, 'El caçador Ben Corbett viatja a Alaska amb la seva dona per supervisar un oleoducte. El descobriment de quatre cadàvers congelats canviarà la vida del caçador, considerat culpable de l\\''assassinat. Ben intenta escapar-se pujat a una avioneta, però quelcom terrible està a punt de passar...', 'Ras i curt un telefilm... que no el salva ni el Rutger Hauer.', 8, 4, 'no', 0, '', '', 'no', '', '');
INSERT INTO `CINEMA` VALUES(27, 'El infierno verde (The Green Inferno)', 2014, 'Eli Roth', 'Lorenza Izzo, Ariel Levy, Daryl Sabara, Sky Ferreira, Aaron Burns', 2, 1, 'Justine i els seus companys activistes viatgen al Perú per impedir que la tala d\\''arbres perturbi la vida d\\''una tribu indígena local. Les coses canvien quan descobreixen que la tribu és caníbal.', 'La Lorenza Izzo és una preciositat... Ariel Levy fa de jueva, i pel nom sembla que l\\''actriu ho és...\r\n', 4, 4, 'no', 0, 'Manuel Riveiro', 'Antonio Quercia', 'no', '', '');
