INSERT INTO `CINEMA` VALUES(33, 'En busca del fuego', 1981, 'Jean-Jacques Annaud', 'Everett McGill, Rae Dawn Chong, Ron Perlman, Nicholas Kadi, Gary Schwart', 11, 3, 'Fa més de 70mil anys, un clan de neandertals que saben mantindre el foc, però no produïrlo, es sentirà desvalgut quan se\\''ls hi apaga el foc, i en conseqüencia tres escollits emprendràn una expedició en la recerca del foc...', '', 3, 9, 'no', 0, '', '', 'no', '', '');
INSERT INTO `CINEMA` VALUES(30, 'Cell', 200, ' Tod Williams', 'John Cusack, Samuel L. Jackson, Isabelle Fuhrman, Owen Teague, Clark Sarullo, Anthony Reynolds, Erin Elisabeth Burns, Stacy Keach', 2, 1, 'El món s\\''en va a norris gràcies a una mena d\\''invasió dels mòbils.\r\n\r\n/Terror/Zombis/', 'Basada en una novel·la d\\''Stephen King, de la que és co-guionista.', 4, 4, 'no', 0, 'Marcelo Zarvos', 'Michael Simmonds', 'yes', '', '');
INSERT INTO `CINEMA` VALUES(44, 'Sinister II', 2015, '', 'James Ransone, Shannyn Sossamon, Robert Daniel Sloan, Dartanian Sloan', 2, 1, 'Després de que Ashley Oswalt descuartitzés a la seva familia i desapareixés amb Mr. Boggie, una mare i els seus fills es muden a la casa on uns nens poseïts van cometre els assassinats. Mr. Boggie ha tornat i assetja a un dels germans...', 'El nen bo (que ha sofert maltractaments del seu pare), quan els nens fantasma li insinuen que ha de fer mal a la mare:!!!Mi madre no ha hecho nada¡¡¡\r\nNen fanstama: Exacto.', 5, 4, 'no', 0, '', '', 'no', '', '');
INSERT INTO `CINEMA` VALUES(45, 'Godzilla', 1998, 'Roland Emmerich', 'Hank Azaria, Jean Reno, Matthew Broderick', 2, 7, 'Les proves nuclears realitzades pels francesos al pacífic originen l\\''aparició a l\\''oceà d\\''un reptil mutant de poderoses dimensions, localitzat després de l\\''atac a un vaixell japonès.', '', 5, 4, 'no', 0, '', '', 'no', '', '');
INSERT INTO `CINEMA` VALUES(46, 'Todos los días de mi vida', 2012, 'Michael Sucsy', 'Rachek McAdams, Channing Tatum, Jessica Lange', 2, 20, 'Un accident de cotxe deixa a Paige en coma, Quan es desperta ha perdut la memòria i no reconeix ni tant sols al seu marit Leo. Llavors aquest tindrà que tornar a conquistar-la. Drama romàntic basat en la història real de Krickitt i Kim Carpenter...', 'El que fa de pare és el protagonista de En la boca del miedo, que ara no recordo el nom...', 5, 4, 'no', 0, '', '', 'no', '', '');
INSERT INTO `CINEMA` VALUES(50, 'Ulises', 1954, 'Dino De Laurentis, Carlo Ponti', 'Kirk Douglas, Silvana Mangano, Anthony Quinn, Rossana Podesta, Jacques Dusmenil, Franco Interlenghi, Elena Zareschi, Evi Maltagliati, Ludmilla Dudarova, Tania Weber, Piero Lulli, Ferruccio Stagni, Alessandro Ferzen, Oscar Andriani, Umberto Silvestri, Gualtiero Tumiati, Teresa Pellati, Mario Feliciani, Michele Riccardini', 8, 16, 'Adaptació de La Odisea d\\''Homer, que narra els esforços d\\''Ulises per arribar a Ítaca després de 10 anys de la guerra de Troia...', '', 4, 4, 'yes', 4, 'Alessandro Cicognini (Diretta da Franco Ferrara)', 'Harold Rosson, A.S.C.', 'yes', '', '');
