INSERT INTO `CINEMA` VALUES(54, 'Hellboy', 2004, 'Guillermo Del Toro', 'Ron Perlman, Selma Blair, Jeffrey Tambor, Karel Roden, John Hurt', 0, 7, 'Finals de la Segona Guerra Mundial. Per mitjà de la màgia negra, els nazis conjuren al dimoni Hellboy, que creix entre els aliats fins convertir-se en adult, actuant com agent de la oficina de defensa i investigació paranormal...', '', 5, 4, 'no', 0, '', '', 'no', '', '');
