INSERT INTO `CINEMA` VALUES(70, 'Centurión', 2009, 'Neil Marshall', 'Michael Fassbender (Centurion Quintus Dias), Andreas Wisniewski (Commander Gratus), Dave Legeno (Vortix)', 1, 3, 'A l\\''any 117 d. C., un grup de soldats romans intenta sobreviure rera les línies enemigues després de que la seva legió hagi sigut aniquilada.', '', 4, 3, 'no', 0, '', 'Sam McCurdy, B.S.C.', 'no', '', '');
INSERT INTO `CINEMA` VALUES(71, 'La princesa prometida', 1987, 'Rob Reiner', 'Robin Wright, Mandy Patinkin, Chris Sarandon, Peter Falk', 2, 26, 'Un avi llegeix un conte al seu net que està malalt. Al conte una princesa que ha sigut segrestada i obligada a casar-se amb el malvat príncep Humperdinck...', '', 3, 4, 'no', 0, '', '', 'no', '', '');
