INSERT INTO `CINEMA` VALUES(73, 'Pactar amb el diable (The devil\\''s advocate)', 1997, '', 'Keanu Reeves, Al Pacino, Charlize Theron', 2, 26, 'Kevin Lomax és un jove advocat molt competent que troba feina en un bufet molt prestigiós. El seu nou cap, John Milton, és un home molt peculiar que li canviarà la vida i fins i tot el caràcter. Mary Ann, la dona de Kevin, no està d\\''acord amb la nova manera de fer del seu marit i comencen els problemes...', '', 8, 0, 'no', 0, '', '', 'no', '', '');
INSERT INTO `CINEMA` VALUES(74, 'Joc d\\''armes (War dogs)', 2016, 'Todd Phillips', 'Jonah Hill, Miles Teller', 2, 2, 'Tracta de com un noi passa de ser massatgiste i venedor de llençols a traficant d\\''armes...\r\n\r\nNo és una comèdia ni un drama...', 'La nòvia del noi és una meravella: investigar...', 3, 4, 'no', 0, '', 'Lawrence Sher', 'no', '', '');
